<script>
import axios from 'axios'
import { exportImgUrl } from '../../main'
export default {
  name: 'MyPage',
  data () {
    return {
      mime: {},
      tabPosition: 'left',
      QuesData: [],
      ReplyData: [],
      LostData: [],
      GetData: [],
      MoodData: [],
      imgUrl: '',
      state: '未找回'
    }
  },
  methods: {
    // 路由跳转
    toQuesDetail (qid) {
      localStorage.setItem('qid', JSON.stringify(qid))
      this.$router.push({
        path: '/question_detail/' + qid
      })
    },
    // 表格展示
    handleEdit (index, row) {
      console.log(index, row)
    },
    handleDeleteQues (index, row) {
      console.log('index-row')
      console.log(row.id)
      var qid = row.id
      axios({
        method: 'delete',
        url: 'quesInformation/del/%7Bqid%7D',
        params: {
          qid: qid
        }
      }).then(resp => {
        if (resp.data.code === 200) {
          this.loadQuesData()
        }
      }).catch(error => error)
    },
    handleDeleteReply (index, row) {
      console.log('index-row')
      console.log(row.id)
      var rid = row.id
      axios({
        method: 'delete',
        url: 'quesReply/del/%7Brid%7D',
        params: {
          rid: rid
        }
      }).then(resp => {
        if (resp.data.code === 200) {
          this.loadReplyData()
        }
      }).catch(error => error)
    },
    handleDeleteLost (index, row) {
      var id = row.id
      axios({
        method: 'delete',
        url: 'lostInformation/del/%7Bid%7D',
        params: {
          id: id
        }
      }).then(resp => {
        if (resp.data.code === 200) {
          this.loadLostData()
        }
      }).catch(error => error)
    },
    handleEditLost (index, row) {
      if (row.stateId === 1) {
        alert('已完成，不可修改！！！')
      } else {
        console.log(row)
        var lid = row.id
        // 页面跳转
        localStorage.setItem('lid', JSON.stringify(lid))
        this.$router.push({
          path: '/lost_edit/' + lid
        })
      }
    },
    handleDeleteGet (index, row) {
      console.log('index-row')
      console.log(row.id)
      var id = row.id
      axios({
        method: 'delete',
        url: 'lostInformation/del/%7Bid%7D',
        params: {
          id: id
        }
      }).then(resp => {
        if (resp.data.code === 200) {
          this.loadGetData()
        }
      }).catch(error => error)
    },
    handleDeleteMood (index, row) {
      console.log('index-row')
      console.log(row.id)
      var id = row.id
      axios({
        method: 'delete',
        url: 'mood/del/%7Bid%7D',
        params: {
          id: id
        }
      }).then(resp => {
        if (resp.data.code === 200) {
          this.loadMoodData()
        }
      }).catch(error => error)
    },
    formatter (row, column) {
      return row.address
    },
    // 加载当前用户数据
    loadMy () {
      if (localStorage.getItem('id') !== null) {
        var _this = this
        axios({
          method: 'post',
          url: 'users/datas/%7Buid%7D',
          params: {
            uid: localStorage.getItem('id')
          }
        }).then(resp => {
          if (resp.data.code === 200) {
            console.log(resp.data.data)
            _this.mime = resp.data.data
            var str = resp.data.data.photoAddress.replace(exportImgUrl, '')
            _this.imgUrl = require('@/assets/' + str)
          }
        }).catch(error => error)
      }
    },
    loadQuesData () {
      var _this = this
      axios({
        method: 'get',
        url: 'quesInformation/data/%7Buid%7D',
        params: {
          uid: localStorage.getItem('id')
        }
      }).then(resp => {
        if (resp.data.code === 200) {
          _this.QuesData = resp.data.data
          console.log('_this.QuesData:' + _this.QuesData)
          console.log(_this.QuesData)
        }
      }).catch(error => error)
    },
    loadReplyData () {
      var _this = this
      axios({
        method: 'get',
        url: 'quesReply/data/%7Buid%7D',
        params: {
          uid: localStorage.getItem('id')
        }
      }).then(resp => {
        if (resp.data.code === 200) {
          _this.ReplyData = resp.data.data
        }
      }).catch(error => error)
    },
    loadLostData () {
      var _this = this
      axios({
        method: 'get',
        url: 'lostInformation/lost/%7Buid%7D',
        params: {
          uid: localStorage.getItem('id')
        }
      }).then(resp => {
        if (resp.data.code === 200) {
          _this.LostData = resp.data.data
        }
      }).catch(error => error)
    },
    loadGetData () {
      var _this = this
      axios({
        method: 'get',
        url: 'lostInformation/get/%7Buid%7D',
        params: {
          uid: localStorage.getItem('id')
        }
      }).then(resp => {
        if (resp.data.code === 200) {
          _this.GetData = resp.data.data
          console.log('_this.QuesData:' + _this.GetData)
          console.log(_this.GetData)
        }
      }).catch(error => error)
    },
    loadMoodData () {
      var _this = this
      axios({
        method: 'get',
        url: 'mood/%7Buid%7D',
        params: {
          uid: localStorage.getItem('id')
        }
      }).then(resp => {
        if (resp.data.code === 200) {
          _this.MoodData = resp.data.data
        }
      }).catch(error => error)
    }
  },
  created () {
    this.loadMy()
    this.loadQuesData()
    this.loadReplyData()
    this.loadLostData()
    this.loadGetData()
    this.loadMoodData()
  }
}
</script>

